        $prodPath = 'images/products/';
        foreach ($categories as $category) {
          $category['image'] = isset($category['image']) && $category['image'] ? $prodPath.$category['image'].'.jpg' : '';
          Category::create($category);
        }

        foreach ($additionalCategories as $category) {
            AddCategory::create($category);
        }

        foreach ($products as $k => $product) {
            $fields = $product;
            $fields['image'] = isset($product['image']) && $product['image'] ? $prodPath.$product['image'].'.jpg' : null;
            $fields['big_image'] = isset($product['image']) && $product['image'] ? $prodPath.$product['image'].'_big.jpg' : null;

            if ($product['parts']) {
                $fields['whole_price'] = 0;
                $fields['part_price'] = $product['price'] ? 0.1 * $product['price'] : 0;

                $fields['action_whole_price'] = 0;
                $fields['action_part_price'] = $product['price'] ? $fields['part_price']/2 : 0;

            } else {
                 $fields['whole_price'] = $product['price'];
                 $fields['part_price'] = 0;

                 $fields['action_whole_price'] = $product['price'] ? $fields['whole_price'] : 0;
                 $fields['action_part_price'] = 0;
            }
            unset($fields['price']);

            $fields['action'] = 0;
            $fields['active'] = true;

            Product::create($fields);
        }
    }
